# affiche le nombre d'éléments dans l'array journalistes
def nombreliste
  puts
  puts "Le nombre de journalistes de la liste est de #{@journalistes.length}"
  continue
end

# affiche le nombre d'éléments contenant au moins un chiffre dans leur nom
def chiffreliste
  journalisteschiffres = @journalistes.grep /[0-9]/ # .grep permet de sélectionner les éléments d'un array contenant au moins un des caractères entre /[]/ ; en l'occurence, il les range dans un nouvel array journalisteschiffres 
  puts "Le nombre de journalistes dont le compte contient un chiffre est de #{journalisteschiffres.length}"
  continue
end

# affiche le nombre d'éléments contenant le terme "Aude"
def aude
  nombreaude = 0
  @journalistes.each do |handle|
    if handle =~ /aude/i
      nombreaude += 1
    end
                    end
  puts "Le nombre de journalistes dont le compte contient Aude est de #{nombreaude}"
  continue
end

# affiche le nombre d'éléments commençant par une majuscule
def majliste
  nombremajfirst = @journalistes.select { |pl|
                                        pl[1] == pl[1].capitalize && pl[1] =~ /[A-Z]/
                                      }
  puts "Le nombre de journalistes dont le compte commence par une majuscule est de #{nombremajfirst.length}"
  continue
end

# affiche le nombre d'éléments contenant une majuscule
def majalllistes
  journalistesmajall = @journalistes.grep /[A-Z]/
  puts "Le nombre de journalistes dont le compte contient une majuscule est de #{journalistesmajall.length}"
  continue
end

# affiche le nombre total d'underscores dans l'array
def underscore
  journalistes_ = 0
  @journalistes.each do |handle|
    array_chars = handle.chars #Création d'un array array_chars contenant séparement chaque charactere de handle  Exemple : Si handle = "blabla" array_chars = [b, l, a, b, l, a]
    array_chars.each do |char_seul|
      if char_seul == "_"
        journalistes_ += 1
      end
                end
                    end
  puts "Le nombre de journalistes dont le compte contient un underscore est de #{journalistes_}"
  continue
end

# affiche une liste triée par ordre alphabétique
def alphabetliste
  puts "DEBUT DE LA LISTE DES JOURNALISTES PAR ORDRE ALPHABETIQUE"
  10.times do print "-" end
  print @journalistes.sort_by { |sorter| sorter.downcase }
  puts
  10. times do print "-" end
  puts
  puts "FIN DE LA LISTE DES JOURNALISTES PAR ORDRE ALPHABETIQUE"
  continue
end

